mapper.select:
select
ta.*, tu.name ownerName
from t_activity ta
    left join t_user tu on tu.id = ta.owner_id


    ${filterSqlDTO.filterSql}


    and ta.owner_id = #{activityPageQueryDTO.ownerId}


    and ta.name like concat('%',#{activityPageQueryDTO.name},'%')


    and ta.start_time >= #{activityPageQueryDTO.startTime}


    and ta.end_time <= #{activityPageQueryDTO.endTime}


    and ta.cost >= #{activityPageQueryDTO.cost}


    and ta.create_time >= #{activityPageQueryDTO.createTime}


order by ta.edit_time desc
